import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

function Item({id, stock, title, price, img}) {
  return (
    <Row xs={1} md={3} className="g-4"><Card style={{ width: '18rem' }}>
        <Card.Img variant="top" src={img} />
        <Card.Body>
          <Card.Title>{title}</Card.Title>
          <Card.Text>
            $ {price}
          </Card.Text>
          <Card.Text>
            Hay {stock} unidades disponibles
          </Card.Text> {/* poner link que vaya a detalle del producto donde voy a usar ID para routeaar */}
          <Button variant="primary">Comprar</Button>
        </Card.Body>
      </Card>
    </Row>
  );
}

export default Item;
