import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function Item({id, stock, title, price, img}) {
  return (
          <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src={img} height="30%"/>
            <Card.Body>
              <Card.Title height="30%">{title}</Card.Title>
              <Card.Text height="30%">
                $ {price}
              </Card.Text>
              <Card.Text height="30%">
                Hay {stock} unidades disponibles
              </Card.Text> {/* poner link que vaya a detalle del producto donde voy a usar ID para routeaar */}
              <Button variant="primary">Comprar</Button>
            </Card.Body>
          </Card>
  );
}

export default Item;
