import Card from 'react-bootstrap/Card';
import { Link } from 'react-router-dom';
import cardStyle from "../styles/Item.css"

function Item({id, stock, title, price, img}) {
  return (
          <Card className="cardStyle" style={{ width: '18rem' , height: "28rem"}}>
            <Card.Img variant="top" src={img} height="60%" width="30%" />
            <Card.Body>
              <Card.Title height="30%">{title}</Card.Title>
              <Card.Text height="30%">
                $ {price}
              </Card.Text>
              <Card.Text height="30%">
                {stock} unidades disponibles
              </Card.Text>
              <Card.Text>
                <Link to={`/item/${id}`}>MÃ¡s detalles...</Link>
              </Card.Text>
            </Card.Body>
          </Card>
  );
}

export default Item;
