import Button from 'react-bootstrap/Button'

let initial = 1

function ItemCount({stock}) {
        const [count, setCount] = useState()

        useEffect(() => {
                setCount(initial)
            }
        ,[])
        
        const add = () => {
            if (count < stock) {
            setCount (count + 1)
            }
        }

        const subtract = () => {
            if (count > stock) {
                setCount (count - 1)
            }}

        return (
            <Container>
                <Button variant="outline-primary" onClick={add}>+</Button>
                <p>{count}</p>
                <Button variant="outline-secondary" onClick={subtract}>-</Button>
            </Container>
        )
}

export default ItemCount
